mapper.select:
select date,staffCode,id,new_id,owePerson,IFNULL(oweDetails,'') as
oweDetails from owes_content
where staffCode=#{staffCode}

	and date=#{date}


	order by date desc

select date,staffCode,id,new_id,owePerson,oweDetails from owes_content
where staffCode=#{staffCode}
and date=#{date}

select r.id,r.department,r.types,(CASE when r.content is null then ''
else r.content end ) content,r.staffCode,r.date from
Reading_reflection r
where staffCode=#{staffCode}

	and date=#{date}


	order by r.date desc

select d.clockDirectory,p.ReadingFeeling,p.clockDate,p.id,b.clockBook as bookNames from reading_detail d INNER JOIN reading_plancontent p on d.id=p.ChapterId INNER JOIN books b on d.BookId=b.ID where staffCode=#{staffCode} and clockDate <=#{clockDate} and p.ReadingFeeling is not null and p.ReadingFeeling<>""
and clockDate=#{date}
order by clockDate desc

select id,actconcom ,date,projectType,entry_name,planTime from work_plan
where staffCode=#{staffCode}

	and date=#{date}


	order by date desc

select date,staffCode,type,Customtype,Customcontent from ( SELECT 5 num,'23:59:59' as date,'头像' as type,`姓名` as Customtype,头像 as Customcontent,人员编码 as staffCode from datacenter_personnel p left JOIN usertable u on u.`员工编码`=p.`人员编码` UNION ALL select 4 num,SUBSTRING(createDate,12,8) date,'总结' as type,actconcom ,staffCode,staffCode from work_plan where date=#{date} UNION ALL select 3 num,SUBSTRING(createTime,12,8) date,'精进' as type,r.types,(CASE when r.content is null then '' else r.content end ) content,staffCode from Reading_reflection r where date=#{date} UNION ALL select 2 num,SUBSTRING(createTime,12,8) date,'感恩' as type,owePerson,oweDetails,staffCode from owes_content oc where oc.date=#{date} UNION ALL select 1 num,SUBSTRING(createTime,12,8) date,'自主' as type,c.Customtype,(CASE when c.Customcontent is null then '' else c.Customcontent end ) Customcontent ,staffCode from custom_reflection c where date=#{date} ) a where staffCode in
#{staffCode}
order by num desc
