mapper.select:
select rp.staffCode, dp.姓名 as staffName, dp.岗位 as staffJobs, bb.id as bookID, bb.clockBook, rp.clockDate, rd.clockDay, rd.clockDirectory, REPLACE(REPLACE(content,CHAR(10),'\n'),CHAR(13),'\n') content, dp.部门 as department, rp.actualReading, rp.ReadingFeeling, rp.actualReading, rp.num from Reading_plancontent rp left join Reading_detail rd on rp.ChapterId=rd.id left join books bb on rd.BookId=bb.id left join datacenter_personnel dp on rp.staffCode=dp.人员编码 where rp.staffCode=#{staffCode}
and rp.clockDate=#{clockDate}
and clockDate >= #{beginClockDate} and clockDate<=#{endClockDate}
and rd.clockDirectory=#{clockDirectory}

mapper.update:
UPDATE
Reading_plancontent
SET actualReading =
now(),timeLength=#{timeLength},ReadingFeeling=#{feeling},num=#{num}
WHERE staffCode=#{staffCode}
and clockDate=#{clockDate}

mapper.sql:
select sum(a) FROM (
select count(praise) as a  from praise  where Be_praised_code=#{staffCode}   
union all
select count(fabulous)*2 as a from fabulous  where Be_fabuloused_code=#{staffCode}  
union all
select SUM(integral) from reading_plancontent WHERE staffCode=#{staffCode} AND integral is not null  
union ALL
select count(actconcom) from work_plan WHERE staffCode=#{staffCode}         
) bb
